import React, { useState, useEffect } from 'react';
import { Typography } from '@material-ui/core';
import { Grid } from '@material-ui/core';
import { Paper } from '@material-ui/core';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import validator from 'validator';
import ErrorOutlineOutlinedIcon from '@material-ui/icons/ErrorOutlineOutlined';
import {useDispatch, useSelector} from "react-redux";
import {updateForm, formActions} from "../store/form-data-slice";
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/material.css';

const useStyles = makeStyles((theme) => ({
    root: {
        paddingLeft: 50,
        paddingRight: 50,
        padding: 20,
        marginTop: 30

    },
    error: {
        maxWidth: 150,
        color: "red"
    },
    errorCard: {
        paddingTop: 15,
        marginTop: 20
    },
    textField: {
        width: 300,
        marginTop: 20
    },
}));


const Step2 = () => {
    const classes = useStyles();
    const dispatch = useDispatch();
    const nextOperations = useSelector((state) =>state.formData.operations.update);
    const formData = useSelector((state) => state.formData.formData);
    const [email, setEmail] = useState("");

    useEffect(() => {

        setEmail(formData.email);
    }, [email])



    const emailChangeHandler = (e) => {
        setEmail(e.target.value);
        if(!validator.isEmail(e.target.value.trim())){
            dispatch(formActions.setOperations({
                function: 'update',
                status: "Failed",
                error: "Invalid email"
            }))
        }
    }

    const dobChangeHandler = (e) => {

    }

    return(
        <Grid
            container
            direction="column"
            justifyContent="center"
            alignItems="center"
        >
        <Paper className={classes.root}  elevation={8} >
        <Grid
            container
            direction="column"
            justifyContent="center"
            alignItems="center"
        >
                <LockOutlinedIcon />

            <Typography component="h1" variant="h5">
             Είσοδος
            </Typography>

            <TextField className={classes.textField} label="email" variant="outlined" value={email}  type="text" name="Email" onChange={emailChangeHandler} ></TextField><br />
            <TextField className={classes.textField} label="date of birth" variant="outlined"  type="text" name="date of birth" onChange={dobChangeHandler} ></TextField><br />
        </Grid>

        {nextOperations.error &&
            <Paper elevation={3} className={classes.errorCard}>
                <Grid
                    container
                    direction="column"
                    justifyContent="center"
                    alignItems="center"
                >

                    <Grid item>
                        <Typography component="div" noWrap={false} paragraph={true} className={classes.error} variant="body1">
                            <ErrorOutlineOutlinedIcon />
                        </Typography>
                    </Grid>
                    <Grid item>
                        <Typography component="div" noWrap={false} paragraph={true} className={classes.error} variant="body1">
                            {nextOperations.error}
                        </Typography>
                    </Grid>

                </Grid>
            </Paper>
        }
    </Paper>
    </Grid>
    );
}

export default Step2;
