import React, { useState, useEffect } from 'react';
import { Typography } from '@material-ui/core';
import { Grid, Container } from '@material-ui/core';
import { Paper } from '@material-ui/core';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import validator from 'validator';
import ErrorOutlineOutlinedIcon from '@material-ui/icons/ErrorOutlineOutlined';
import {useDispatch, useSelector} from "react-redux";
import {updateForm, formActions} from "../store/form-data-slice";
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/material.css';

// Styles for the component
const useStyles = makeStyles((theme) => ({
    root: {
        padding: 20,
        marginTop: 30,
    },
    error: {
        maxWidth: 150,
        color: "red"
    },
    errorCard: {
        paddingTop: 15,
        marginTop: 20
    },
    textField: {
        // width: 300,
        marginTop: 20,
        marginBottom: 20
    },
}));


const Step1 = () => {
    const classes = useStyles();
    const [phoneNumber, setPhoneNumber] = useState("");
    const dispatch = useDispatch();
    const nextOperations = useSelector((state) =>state.formData.operations.update);
    const formData = useSelector((state) => state.formData.formData);


    useEffect(() => {
        // Set phone number to input when you come back from another step
        setPhoneNumber(formData.phoneNumber);
    }, [formData.phoneNumber])


    // Handler function for name input
    const nameChangeHandler = (e) => {
        if(validator.isLength(e.target.value.trim(), {min: 3, max: 10}) ){
            dispatch(formActions.setOperations({
                function: 'update',
                status: "Success",
            }))

            // Pass the data to the store
            dispatch(updateForm({name: e.target.value.trim()}));
        }else{
            // Set a new error 
            dispatch(formActions.setOperations({
                function: 'update',
                status: "Pending",
                error: "Name must be between 3 and 10 characters!"
            }));
        }
    }




    return(
    <Container >
    <Grid container spacing={2}>
      <Grid item xs={12} md={6} container direction="column" justifyContent="center" alignItems="center">
        <Paper className={classes.root}  elevation={8}>

            <TextField className={classes.textField} label="name" defaultValue={formData?.name} variant="outlined"  type="text" name="Name" onChange={nameChangeHandler} ></TextField><br />
            {/* <PhoneInput  placeholder="Enter phone number"  value={phoneNumber} onChange={ (phone) => { dispatch(updateForm({phoneNumber: phone}))}} country={'gr'}/> */}


        {nextOperations.error &&
            <Paper elevation={3} className={classes.errorCard}>
                <Grid
                    container
                    direction="column"
                    justifyContent="center"
                    alignItems="center"
                >

                    <Grid item>
                        <Typography component="div" noWrap={false} paragraph={true} className={classes.error} variant="body1">
                            <ErrorOutlineOutlinedIcon />
                        </Typography>
                    </Grid>
                    <Grid item>
                        <Typography component="div" noWrap={false} paragraph={true} className={classes.error} variant="body1">
                            {nextOperations.error}
                        </Typography>
                    </Grid>

                </Grid>
            </Paper>
        }

        </Paper>        
      </Grid>
    </Grid>
  </Container>
    //     <Container>
    //     <Grid
    //         container
    //         direction="column"
    //         justifyContent="center"
    //         alignItems="center"
    //         spacing={2}
