    //         <TextField className={classes.textField} label="name" defaultValue={formData?.name} variant="outlined"  type="text" name="Name" onChange={nameChangeHandler} ></TextField><br />
    //         <PhoneInput  placeholder="Enter phone number"  value={phoneNumber} onChange={ (phone) => { dispatch(updateForm({phoneNumber: phone}))}} country={'gr'}/>

    //     </Grid>

    //     {nextOperations.error &&
    //         <Paper elevation={3} className={classes.errorCard}>
    //             <Grid
    //                 container
    //                 direction="column"
    //                 justifyContent="center"
    //                 alignItems="center"
    //             >

    //                 <Grid item>
    //                     <Typography component="div" noWrap={false} paragraph={true} className={classes.error} variant="body1">
    //                         <ErrorOutlineOutlinedIcon />
    //                     </Typography>
    //                 </Grid>
    //                 <Grid item>
    //                     <Typography component="div" noWrap={false} paragraph={true} className={classes.error} variant="body1">
    //                         {nextOperations.error}
    //                     </Typography>
    //                 </Grid>

    //             </Grid>
    //         </Paper>
    //     }
    // </Paper>
    // </Grid>
    // </Container>
    );
}

export default Step1;
